function menucalcs(){
	var currentScroll = $(window).scrollTop();
	
	if ($(window).width()>991){
		var nbbfs = 0;
		var s = 30;
		var nbbfss = nbbfs - currentScroll;
		
		nbbfs = nbbfss < -s?-s:nbbfss;
		
		var fs = 46;
		var sfs = 34;
		
		
		var p = 0;
		p = (currentScroll/s)*100;
		
		var t = fs - sfs;
		t =  (p/100)*t;
		
		fs = fs - t;
		fs = fs<sfs?sfs:fs;
		
		
		
		
		var t = fs /2;
		t =  (p/100)*t;
		
		var fs_mt = fs - t;
		fs_mt = fs - fs_mt;
		fs_mt = fs_mt > fs/2?fs/2:fs_mt;



//	console.log("nbbfs:"+nbbfs+" | s:"+s+" | currentScroll:"+currentScroll+" | fs:"+fs+" | p:"+p+" | fs_mt:"+fs_mt)
		$("#main-nav").css({
			"margin-top":nbbfs
		})
		$(".navbar-brand").css({
			"font-size":fs,
			"margin-bottom":-fs,
			"margin-top":fs_mt,
		})
	} else {
		
		
		$(".navbar-brand").css({
			"font-size":34,
			"margin-bottom":-34,
			"margin-top":2,
		})
	}
	
	
	
}
